
public class ted05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		 int i = 1;
	        while (i <= 20) {
	            System.out.print(i);
	            if (i % 5 == 0){
	                System.out.println();
	            }
	            else {
	                System.out.print('\t');
	            }
	            ++i;
	        }
	    
	}

}
